SELECT A.fd_mrclist_bkordernumber AS '银联订单号', CONCAT(B.fd_author_truename, '-', B.fd_author_username) AS '客户名称', A.fd_mrclist_paydate AS '充值日期', A.fd_mrclist_rechaphone AS '充值手机号码', A.fd_mrclist_rechamoney AS '充值金额', A.fd_mrclist_bkmoney AS '支付金额' FROM tb_mobilerechargelist AS A LEFT JOIN tb_author AS B ON A.fd_mrclist_authorid = B.fd_author_id WHERE A.fd_mrclist_payrq = '00' AND A.fd_mrclist_paydate >= '2014-08-01' AND A.fd_mrclist_paydate <= '2014-08-31' ORDER BY A.fd_mrclist_id DESC